CREATE DEFINER=`root`@`localhost` PROCEDURE `search_by_code`(code BIGINT)
BEGIN
	SELECT
		p.barcode,
		p.name,
		r.name,
		m.name,
		m.country
	FROM
		products p
		JOIN release_forms r ON
			p.release_forms_id = r.id
		JOIN manufacturers m ON
			p.manufacturer_id = m.id
	WHERE
		p.barcode = code;
END ;;
